import React from 'react';
import './Contact.css';

function Contact() {
  const contactInfo = [
    {
      title: 'Email',
      detail: 'your.email@example.com',
      link: 'mailto:your.email@example.com',
      icon: 'üìß'
    },
    {
      title: 'Phone',
      detail: '+1 (555) 123-4567',
      link: 'tel:+15551234567',
      icon: 'üì±'
    },
    {
      title: 'Location',
      detail: 'Your City, State',
      link: null,
      icon: 'üìç'
    },
  ];

  return (
    <section id="contact" className="contact-section">
      <div className="contact-container">
        <div className="contact-info">
          <h3 className="info-title">Contact Information</h3>
          {contactInfo.map((info, index) => (
            <div key={index} className="contact-card">
              <span className="contact-icon">{info.icon}</span>
              {info.link ? (
                <span><strong>{info.title}:</strong> <a href={info.link} className="contact-value">{info.detail}</a></span>
              ) : (
                <span><strong>{info.title}:</strong> <span className="contact-value">{info.detail}</span></span>
              )}
            </div>
          ))}

          <div className="social-media">
            <h4 className="social-title">Follow Me</h4>
            <div className="social-links">
              <a href="#" className="social-link github">
                <span className="social-icon">üêô</span> GitHub
              </a>
              <a href="#" className="social-link linkedin">
                <span className="social-icon">üíº</span> LinkedIn
              </a>
              <a href="#" className="social-link twitter">
                <span className="social-icon">üê¶</span> Twitter
              </a>
              <a href="#" className="social-link instagram">
                <span className="social-icon">üì∏</span> Instagram
              </a>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Contact;
